    public void render() { // draw the world
        coinRotateTimer--;
        if(coinRotateTimer<-GRID_UNIT_SIZE/3)
            coinRotateTimer = GRID_UNIT_SIZE/3;
        
    int position_x = 0;
    int position_y = 0;
    int GoombaCount = 0;
    
    for(int i=0;i<GRID_UNITS_WIDE;i++) { // for each column
      for(int ii=0;ii<GRID_UNITS_TALL;ii++) { // for each tile in that column
        //engine.rect(i*GRID_UNIT_SIZE,ii*GRID_UNIT_SIZE, GRID_UNIT_SIZE-1,GRID_UNIT_SIZE-1);
        position_x  = i;
        position_x = position_x * 16;
        position_y = ii;
        position_y = position_y * 16;
        switch(worldGrid[ii][i]) { // check which tile type it is
          case TILE_SOLID:
            engine.image(breakingBrick,position_x , position_y);
            break;
          case TILE_KILLBLOCK:
            break;
          case TILE_SOLID_BrickBlock:
              engine.image(brickBlock,position_x , position_y);
              break;
          case TILE_SOLID_BONUS:
              engine.image(BonusBlock,position_x , position_y);
              break;
          case TILE_SOLID_BlockBlock:
              engine.image(BlockBlock,position_x , position_y);
              break;
          case TILE_PIPE_LEFT_TOP:
              engine.image(PipeLeftTop,position_x , position_y);
              break;
          case TILE_PIPE_RIGHT_TOP:
              engine.image(PipeRightTop,position_x , position_y);
              break;
          case TILE_PIPE_LEFT_BOTTOM:
              engine.image(PipeLeftBottom,position_x , position_y);
              break;
          case TILE_PIPE_RIGHT_BOTTOM:
              engine.image(PipeRightBottom,position_x , position_y);
              break;
          case TILE_FLAG:
              engine.image(Flag,position_x-8 , position_y);
              break;
          case TILE_CASTLE:
              engine.image(Castle,position_x , position_y);
              break;
          case TILE_CLOUD1:
              engine.image(Cloud1,position_x , position_y);
              break;
          case TILE_CLOUD2:
              engine.image(Cloud2,position_x , position_y);
              break;
          case TILE_CLOUD3:
              engine.image(Cloud3,position_x , position_y);
              break;
          case TILE_MOUNTAIN_LITTLE:
              engine.image(MountainSmall,position_x , position_y);
              break;
          case TILE_MOUNTAIN_BIG:
              engine.image(MountainBig,position_x , position_y);
              break;
          case TILE_SOLID_BONUS_EMPTY:
              engine.image(BonusBlockEmpty,position_x , position_y);
              break;
          case TILE_BIGCASTLE:
              engine.image(CastleBig,position_x , position_y);
              break;
          case TILE_GOOMBA:
              if (GoombaDraw[GoombaCount][1] == 0){
                goomba[GoombaCount].position.x = position_x;
                goomba[GoombaCount].position.y = position_y;
                goomba[GoombaCount].realyXPosition = position_x;
                goomba[GoombaCount].realyYPosition = position_y;
                GoombaDraw[GoombaCount][1] = 1;
              }
              GoombaCount++;
              break;
          default:
            engine.stroke(245); // faint light outline. set to 255 (white) to remove entirely.
            engine.fill(255); // white
            break;
        }
                if(worldGrid[ii][i]==TILE_COIN) { // if it's a coin, draw the coin
                    engine.image(coins, position_x, position_y);
                }
            }
        }
        engine.translate(0, 0);
        for(int i=0; i<50; i++){
            goomba[i].draw();
            goomba[i].move();
            goomba[i].inputCheck();
        }
    }

    static public void gameWin(){
        sound.StopStandartLevelSound();
        if (varLaunchMarioWinSound == 0){
            Sound s = new Sound(engine);
            s.LaunchWinSound();
            varLaunchMarioWinSound = 1;
        }
    }

